CREATE DATABASE `movies_app`;


/*
Create table
*/
DROP TABLE IF EXISTS `movies_app`.`movie_details`;
CREATE TABLE  `movies_app`.`movie_details` (
  `movie_id` int(11) NOT NULL AUTO_INCREMENT,
  `movie_name` varchar(500) DEFAULT NULL,
  `has_won_oscar` tinyint(1) DEFAULT NULL,
  `theatre_gross` double DEFAULT NULL,
  `average_rating` float DEFAULT NULL,
  `created` date DEFAULT NULL,
  `is_deleted` tinyint(1) DEFAULT NULL,
  `imdb_id` varchar(50) NOT NULL,
  `total_ratings` int(11) NOT NULL DEFAULT 0,
  `poster` varchar(500) NOT NULL,
  PRIMARY KEY (`movie_id`)
) ENGINE=InnoDB AUTO_INCREMENT=486 DEFAULT CHARSET=utf8mb4;


